sys_variable_value.value:
(function execute(inputs, outputs) {

  var input = inputs.input.toString();
  var context = inputs.context.toString();
  var serviceMapID = inputs.service_map_sys_id.toString();

  var service = new GlideRecord('sn_fcms_intg_service_map');
  service.get(serviceMapID);

  input = JSON.parse(input);
  context = JSON.parse(context);
  var payloadGenerator = PayloadGeneratorFactory.findGenerator(service, input, context);
  var payload = payloadGenerator.generatePayload();

  gs.debug('FCA Prepared Payload : ' + payload);

  outputs.payload = payload;

})(inputs, outputs);

