sys_script_include.script:
var SnowSOAPMessageUtil = Class.create();
SnowSOAPMessageUtil.prototype = {

	initialize: function() {},

	getSOAPMessage: function(soapMessageSysID) {
		var soapMessage = new GlideRecord('sys_soap_message');
		soapMessage.get(soapMessageSysID);
		return soapMessage;
	},

	getSOAPMessageFunction: function(soapMessageFunctionSysID) {
		var soapMessageFunction = new GlideRecord('sys_soap_message_function');
		soapMessageFunction.get(soapMessageFunctionSysID);
		return soapMessageFunction;
	},

	type: 'SnowSOAPMessageUtil'
};
