sys_variable_value.value:
(function execute(inputs, outputs) {
  var builder = AuthorizationBuilderFactory.findBuilder(inputs['erp_source'], inputs['credential_alias']);
  if(builder){
    var authObj= builder.buildAuthObject();
    for (var name in authObj) 
      outputs[name]=authObj[name];
  }
})(inputs, outputs);

