sys_variable_value.value:
(function execute(inputs, outputs) {
	var response = {};
	response.status = inputs.status_code;
	response.response = {};
	response.response.header = inputs.response_header;
	response.response.body = inputs.response_body;
	response.error = {};
	response.error.code = inputs.error_code;
	response.error.error_message = inputs.error_message;
	outputs.payload = JSON.stringify(response);
})(inputs, outputs);

